CREATE DATABASE RETAIL;
USE RETAIL;

CREATE TABLE CUSTOMER(CUSTID INT PRIMARY KEY AUTO_INCREMENT, FULLNAME VARCHAR(100), EMAIL VARCHAR(100), PHONE VARCHAR(15), ADDRESS VARCHAR(200));
CREATE TABLE CATEGORY(CATID INT PRIMARY KEY AUTO_INCREMENT, CATNAME VARCHAR(50));
CREATE TABLE SUPPLIER(SUPID INT PRIMARY KEY AUTO_INCREMENT, SUPNAME VARCHAR(100), SUPEMAIL VARCHAR(100));
CREATE TABLE PRODUCT(PRODID INT PRIMARY KEY AUTO_INCREMENT, PRODNAME VARCHAR(100), PRICE DECIMAL(10,2), STOCK INT, CATID INT, SUPID INT, FOREIGN KEY (CATID) REFERENCES CATEGORY(CATID), FOREIGN KEY (SUPID) REFERENCES SUPPLIER(SUPID));
CREATE TABLE ORDERS(ORDERID INT AUTO_INCREMENT, CUSTID INT, ORDERDATE DATE, PRODID INT, QTY INT, AMT DECIMAL(10,2), PRIMARY KEY(ORDERID, PRODID), FOREIGN KEY (CUSTID) REFERENCES CUSTOMER(CUSTID), FOREIGN KEY (PRODID) REFERENCES PRODUCT(PRODID));

